@read_only = true
callback = ->(status : BufferMapAsyncStatus) do
  @status = status
end
callback_boxed = Box.box(callback)
@@callback_boxes.push(callback_boxed)
LibWGPU.buffer_map_async(@id, LibWGPU::MapMode::Read, start, size, ->(status, data) do
  cb = Box(typeof(callback)).unbox(data)
  cb.call(status)
end, callback_boxed)

@read_only = false
callback = ->(status : BufferMapAsyncStatus) do
  @status = status
end
callback_boxed = Box.box(callback)
@@callback_boxes.push(callback_boxed)
LibWGPU.buffer_map_async(@id, LibWGPU::MapMode::Write, start, size, ->(status : BufferMapAsyncStatus, data : ::Pointer(Void)) do
  cb = Box(typeof(callback)).unbox(data)
  cb.call(status)
end, callback_boxed)

metal_layer_descriptor = LibWGPU::SurfaceDescriptorFromMetalLayer.new(chain: LibWGPU::ChainedStruct.new(s_type: LibWGPU::SType::SurfaceDescriptorFromMetalLayer), layer: layer)
descriptor = LibWGPU::SurfaceDescriptor.new(next_in_chain: (pointerof(metal_layer_descriptor)).as(::Pointer(LibWGPU::ChainedStruct)), label: label)
return self.new(descriptor)

windows_hwnd_descriptor = LibWGPU::SurfaceDescriptorFromWindowsHWND.new(chain: LibWGPU::ChainedStruct.new(s_type: LibWGPU::SType::SurfaceDescriptorFromWindowsHWND), hinstance: hinstance, hwnd: hwnd)
descriptor = LibWGPU::SurfaceDescriptor.new(next_in_chain: (pointerof(windows_hwnd_descriptor)).as(::Pointer(LibWGPU::ChainedStruct)), label: label)
return self.new(descriptor)

xlib_descriptor = LibWGPU::SurfaceDescriptorFromXlib.new(chain: LibWGPU::ChainedStruct.new(s_type: LibWGPU::SType::SurfaceDescriptorFromXlib), display: display, window: window)
descriptor = LibWGPU::SurfaceDescriptor.new(next_in_chain: (pointerof(xlib_descriptor)).as(::Pointer(LibWGPU::ChainedStruct)), label: label)
return self.new(descriptor)
